import axios from "axios";
import { useState } from "react";

function ProjectProposal() {
  const [service_start_date, setService_start_date] = useState();
  const [service_end_date, setService_end_date] = useState("");
  const [category, setCategory] = useState("");
  const [target_price, setTarget_price] = useState(0);
  const [title, setTitle] = useState("");
  const [subtitle, setSubtitle] = useState("");
  const [image_name, setImage] = useState("");
  const [contents, setcontents] = useState("");

  // const [imageSrc, setImageSrc] = useState([]);
  // const [imageLoading, setImageLoading] = useState(false);

  // const [index, setIndex] = useState(0);

  const onCancelClick = () => {
    window.location.href = "/";
  };
  const onStartDate = (e) => {
    setService_start_date(e.target.value);
  };
  const onEndDate = (e) => {
    setService_end_date(e.target.value);
  };
  const onCategory = (e) => {
    setCategory(e.target.value);
  };
  const onPrice = (e) => {
    setTarget_price(e.target.value);
  };
  const onTitle = (e) => {
    setTitle(e.target.value);
  };
  const onSubTitle = (e) => {
    setSubtitle(e.target.value);
  };
  const onImage = (e) => {
    setImage(e.target.value);
  };
  const onContents = (e) => {
    setcontents(e.target.value);
  };

  // const Check=()=>{

  // }

  const onSubmitClick = () => {
    const data = new FormData();
    data.append("service_start_date", service_start_date);
    data.append("service_end_date", service_end_date);
    data.append("category", category);
    data.append("target_price", target_price);
    data.append("title", title);
    data.append("subtitle", subtitle);
    data.append("image_name", image_name);
    data.append("contents", contents);
    axios
      .post("http://localhost:8090/save", data)
      .then((Response) => {
        console.log(Response);
        window.location.href = "/";
      })
      .catch((error) => {
        console.log(error);
      });
  };
  // const onImageChange = (e) => {
  //   const reader = new FileReader();
  //   reader.readAsDataURL(e.target.files[0]);
  //   //이미지 파일 ㅈ체를 여기에 저ㅏㅇ
  //   setImage(e.target.files[0])
  //   setIndex(index + 1);
  //   return new Promise((resolve) => {
  //       reader.onload = () => {
  //           //결과를 src에 저장
  //           const src = [...imageSrc];
  //           src.push(reader.result);
  //           //배열로 이미지 소스에 집어 넣음
  //           setImageSrc(src);
  //           setImageLoading(true);
  //           resolve();
  //       };
  //   });
  // };

  return (
    <>
      <div className="h-screen flex bg-green[#000333] ">
        <div
          className="hidden lg:flex w-full lg:w-2/5 login_img_section
              justify-around items-center "
          style={{
            backgroundImage: `url("https://images.unsplash.com/photo-1579621970563-ebec7560ff3e?ixlib=rb-1.2.1&amp;ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&amp;auto=format&amp;fit=crop&amp;w=1951&amp;q=80")`,
            backgroundPosition: "center",
            backgroundRepeat: "no-repeat",
            backgroundSize: "cover",
            backgroundColor: "(0, 0, 0, 0.5)",
          }}
        >
          <div
            className=" 
                      bg-black 
                      opacity-20 
                      inset-0 
                      z-0"
          ></div>
          <div className="w-full mx-auto px-20 flex-col items-center space-y-6">
            <h1 className="text-white font-bold text-4xl font-sans">Doneasy</h1>
            <p className="text-white mt-1">Give 해주세요</p>
            <div className="flex justify-center lg:justify-start mt-6 ">
              <button
                className="hover:bg-[#719E71] hover:text-white hover:-translate-y-1 transition-all duration-500 bg-white text-indigo-800 mt-4 px-4 py-2 rounded-2xl font-bold mb-2"
                onClick={onCancelClick}
              >
                {" "}
                이전페이지로{" "}
              </button>
            </div>
          </div>
        </div>

        <div className="flex w-full lg:w-3/5 justify-center items-center bg-gray-50 space-y-8">
          <div className="w-full px-8 md:px-32 lg:px-24">
            <div align="center">
              <h1 className="text-gray-800 font-bold text-2xl mb-10 ">
                프로젝트 제안서
              </h1>
            </div>

            <div align="center" className="mb-5 block   text-[#07074D]">
              <label htmlFor="startday">시작 :　</label>
              <input
                value={service_start_date}
                type="date"
                className='"w-full resize-none rounded-md border border-[#e0e0e0] bg-white py-1 px-2 text-base font-medium text-[#6B7280] outline-none focus:border-[#6A64F1] focus:shadow-md'
                onChange={onStartDate}
              />
              <label htmlFor="endday">종료 :　</label>
              <input
                value={service_end_date}
                type="date"
                className='"w-full resize-none rounded-md border border-[#e0e0e0] bg-white py-1 px-2 text-base font-medium text-[#6B7280] outline-none focus:border-[#6A64F1] focus:shadow-md'
                onChange={onEndDate}
              />

              <select className="ml-20" value={category} onChange={onCategory}>
                <option>-　　select category.　　-</option>
                <option value="seniorcitizen">노인</option>
                <option value="children">아동</option>
                <option value="youth">청년</option>
                <option value="woman">여성</option>
                <option value="disabled ">장애인</option>
              </select>

              <input
                type="number"
                className=" pl-2 w-full outline-none border-none"
                value={target_price}
                placeholder="목표 금액을 설정해주세요."
                onChange={onPrice}
              />
            </div>

            <div className="flex items-center border-2 mb-4 py-2 px-3 rounded-2xl">
              <input
                id="text"
                className=" pl-2 w-full outline-none border-none"
                type="text"
                value={title}
                onChange={onTitle}
                placeholder="주제를 입력하세요."
              />
            </div>
            <div className="flex items-center border-2 mb-6 py-2 px-3 rounded-2xl ">
              <input
                className="pl-2 w-full outline-none border-none"
                type="text"
                value={subtitle}
                id="subTitle"
                onChange={onSubTitle}
                placeholder="부제목을 입력하세요."
              />
            </div>

            <div className="mx-auto w-full max-w-[600px] bg-white">
              <div className="mb-6 pt-4">
                <div className="mb-8">
                  <input
                    type="file"
                    value={image_name}
                    id="file"
                    className="sr-only"
                    onChange={onImage}
                  />
                  <label
                    htmlFor="file"
                    className="relative flex min-h-[200px] items-center justify-center rounded-md border border-dashed border-[#e0e0e0] p-12 text-center"
                  >
                    <div>
                      <span className="mb-2 block text-xl font-semibold text-[#07074D]">
                        Drop files here
                      </span>
                      <span className="mb-2 block text-base font-medium text-[#6B7280]">
                        Or
                      </span>
                      <span className="inline-flex rounded border border-[#e0e0e0] py-2 px-7 text-base font-medium text-[#07074D] bg-gray-200 px-4 py-2 transition duration-300 hover:bg-gray-400">
                        Browse
                      </span>
                    </div>
                  </label>
                </div>

                <div className="mb-1 mt-1 rounded-md bg-[#F5F7FB] py-2 px-4">
                  <div className="flex items-center justify-between">
                    <span className="truncate pr-3 text-base font-medium text-[#07074D]">
                      banner-design.png
                    </span>

                    <button className="text-[#07074D]">
                      <svg
                        width="10"
                        height="10"
                        viewBox="0 0 10 10"
                        fill="none"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          fillRule="evenodd"
                          clipRule="evenodd"
                          d="M0.279337 0.279338C0.651787 -0.0931121 1.25565 -0.0931121 1.6281 0.279338L9.72066 8.3719C10.0931 8.74435 10.0931 9.34821 9.72066 9.72066C9.34821 10.0931 8.74435 10.0931 8.3719 9.72066L0.279337 1.6281C-0.0931125 1.25565 -0.0931125 0.651788 0.279337 0.279338Z"
                          fill="currentColor"
                        />
                        <path
                          fillRule="evenodd"
                          clipRule="evenodd"
                          d="M0.279337 9.72066C-0.0931125 9.34821 -0.0931125 8.74435 0.279337 8.3719L8.3719 0.279338C8.74435 -0.0931127 9.34821 -0.0931123 9.72066 0.279338C10.0931 0.651787 10.0931 1.25565 9.72066 1.6281L1.6281 9.72066C1.25565 10.0931 0.651787 10.0931 0.279337 9.72066Z"
                          fill="currentColor"
                        />
                      </svg>
                    </button>
                  </div>
                </div>
              </div>
            </div>

            <div className="mb-5">
              <label
                htmlFor="message"
                className="mb-3 block text-base font-medium text-[#07074D]"
              ></label>
              <textarea
                rows="4"
                name="message"
                id="message"
                value={contents}
                onChange={onContents}
                placeholder="Type"
                className="w-full resize-none rounded-md border border-[#e0e0e0] bg-white py-3 px-6 text-base font-medium text-[#6B7280] outline-none focus:border-[#6A64F1] focus:shadow-md"
              ></textarea>
            </div>

            <details>
              <summary
                className="rounded-md bg-gray-200 px-4 py-2 transition duration-300 hover:bg-gray-400 space-x-1 text-gray-700 mt-5 mb-5"
                align="center"
              >
                추가 작성 1
              </summary>

              <div className="flex items-center border-2 mb-6 py-2 px-3 rounded-2xl ">
                <input
                  className="pl-2 w-full outline-none border-none"
                  type="text"
                  // value={subtitle}
                  // onChange={onSubTitle}
                  id="subTitle"
                  placeholder="부제목을 입력하세요."
                />
              </div>

              <div className="mx-auto w-full max-w-[600px] bg-white">
                <div className="mb-6 pt-4">
                  <div className="mb-8">
                    <input
                      type="file"
                      // value={image_name}
                      // onChange={onImage}
                      id="file"
                      className="sr-only"
                    />
                    <label
                      htmlFor="file"
                      className="relative flex min-h-[200px] items-center justify-center rounded-md border border-dashed border-[#e0e0e0] p-12 text-center"
                    >
                      <div>
                        <span className="mb-2 block text-xl font-semibold text-[#07074D]">
                          Drop files here
                        </span>
                        <span className="mb-2 block text-base font-medium text-[#6B7280]">
                          Or
                        </span>
                        <span className="inline-flex rounded border border-[#e0e0e0] py-2 px-7 text-base font-medium text-[#07074D] bg-gray-200 px-4 py-2 transition duration-300 hover:bg-gray-400">
                          Browse
                        </span>
                      </div>
                    </label>
                  </div>

                  <div className="mb-1 mt-1 rounded-md bg-[#F5F7FB] py-2 px-4">
                    <div className="flex items-center justify-between">
                      <span className="truncate pr-3 text-base font-medium text-[#07074D]">
                        banner-design.png
                      </span>

                      <button className="text-[#07074D]">
                        <svg
                          width="10"
                          height="10"
                          viewBox="0 0 10 10"
                          fill="none"
                          xmlns="http://www.w3.org/2000/svg"
                        >
                          <path
                            fillRule="evenodd"
                            clipRule="evenodd"
                            d="M0.279337 0.279338C0.651787 -0.0931121 1.25565 -0.0931121 1.6281 0.279338L9.72066 8.3719C10.0931 8.74435 10.0931 9.34821 9.72066 9.72066C9.34821 10.0931 8.74435 10.0931 8.3719 9.72066L0.279337 1.6281C-0.0931125 1.25565 -0.0931125 0.651788 0.279337 0.279338Z"
                            fill="currentColor"
                          />
                          <path
                            fillRule="evenodd"
                            clipRule="evenodd"
                            d="M0.279337 9.72066C-0.0931125 9.34821 -0.0931125 8.74435 0.279337 8.3719L8.3719 0.279338C8.74435 -0.0931127 9.34821 -0.0931123 9.72066 0.279338C10.0931 0.651787 10.0931 1.25565 9.72066 1.6281L1.6281 9.72066C1.25565 10.0931 0.651787 10.0931 0.279337 9.72066Z"
                            fill="currentColor"
                          />
                        </svg>
                      </button>
                    </div>
                  </div>
                </div>
              </div>

              <div className="mb-5">
                <label
                  htmlFor="message"
                  className="mb-3 block text-base font-medium text-[#07074D]"
                ></label>
                <textarea
                  // onChange={onContents}
                  // value={contents}
                  rows="4"
                  name="message"
                  id="message"
                  placeholder="Type"
                  className="w-full resize-none rounded-md border border-[#e0e0e0] bg-white py-3 px-6 text-base font-medium text-[#6B7280] outline-none focus:border-[#6A64F1] focus:shadow-md"
                ></textarea>
              </div>
            </details>

            <details>
              <summary
                className="rounded-md bg-gray-200 px-4 py-2 transition duration-300 hover:bg-gray-400 space-x-1 text-gray-700 mt-0 mb-3"
                align="center"
              >
                추가 작성 2
              </summary>

              <div className="flex items-center border-2 mb-6 py-2 px-3 rounded-2xl ">
                <input
                  className="pl-2 w-full outline-none border-none"
                  type="text"
                  // value={subtitle}
                  // onChange={onSubTitle}
                  id="subTitle"
                  placeholder="부제목을 입력하세요."
                />
              </div>

              <div className="mx-auto w-full max-w-[600px] bg-white">
                <div className="mb-6 pt-4">
                  <div className="mb-8">
                    <input
                      type="file"
                      // value={image_name}
                      // onChange={onImage}
                      id="file"
                      className="sr-only"
                    />
                    <label
                      htmlFor="file"
                      className="relative flex min-h-[200px] items-center justify-center rounded-md border border-dashed border-[#e0e0e0] p-12 text-center"
                    >
                      <div>
                        <span className="mb-2 block text-xl font-semibold text-[#07074D]">
                          Drop files here
                        </span>
                        <span className="mb-2 block text-base font-medium text-[#6B7280]">
                          Or
                        </span>
                        <span className="inline-flex rounded border border-[#e0e0e0] py-2 px-7 text-base font-medium text-[#07074D] bg-gray-200 px-4 py-2 transition duration-300 hover:bg-gray-400">
                          Browse
                        </span>
                      </div>
                    </label>
                  </div>

                  <div className="mb-1 mt-1 rounded-md bg-[#F5F7FB] py-2 px-4">
                    <div className="flex items-center justify-between">
                      <span className="truncate pr-3 text-base font-medium text-[#07074D]">
                        banner-design.png
                      </span>

                      <button className="text-[#07074D]">
                        <svg
                          width="10"
                          height="10"
                          viewBox="0 0 10 10"
                          fill="none"
                          xmlns="http://www.w3.org/2000/svg"
                        >
                          <path
                            fillRule="evenodd"
                            clipRule="evenodd"
                            d="M0.279337 0.279338C0.651787 -0.0931121 1.25565 -0.0931121 1.6281 0.279338L9.72066 8.3719C10.0931 8.74435 10.0931 9.34821 9.72066 9.72066C9.34821 10.0931 8.74435 10.0931 8.3719 9.72066L0.279337 1.6281C-0.0931125 1.25565 -0.0931125 0.651788 0.279337 0.279338Z"
                            fill="currentColor"
                          />
                          <path
                            fillRule="evenodd"
                            clipRule="evenodd"
                            d="M0.279337 9.72066C-0.0931125 9.34821 -0.0931125 8.74435 0.279337 8.3719L8.3719 0.279338C8.74435 -0.0931127 9.34821 -0.0931123 9.72066 0.279338C10.0931 0.651787 10.0931 1.25565 9.72066 1.6281L1.6281 9.72066C1.25565 10.0931 0.651787 10.0931 0.279337 9.72066Z"
                            fill="currentColor"
                          />
                        </svg>
                      </button>
                    </div>
                  </div>
                </div>
              </div>

              <div className="mb-5">
                <label
                  htmlFor="message"
                  className="mb-3 block text-base font-medium text-[#07074D]"
                ></label>
                <textarea
                  // onChange={onContents}
                  // value={contents}
                  rows="4"
                  name="message"
                  id="message"
                  placeholder="Type"
                  className="w-full resize-none rounded-md border border-[#e0e0e0] bg-white py-3 px-6 text-base font-medium text-[#6B7280] outline-none focus:border-[#6A64F1] focus:shadow-md"
                ></textarea>
              </div>
            </details>

            <button
              type="submit"
              className="block w-full bg-[#719E71] mt-5 py-2 rounded-2xl hover:bg-indigo-700 hover:-translate-y-1 transition-all duration-500 text-white font-semibold mb-2"
              onClick={onSubmitClick}
            >
              제출
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

export default ProjectProposal;
